package week1.day;

public class PrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
			for (int i = 2; i<=50; i++) {
				if (i %2==0) {
					System.out.println(i + " is a prime number");
				}
				else {
					System.out.println(i + " is not a prime number");
				}
			}
	

	}
}